/* Access factory */
final GordianFactory myBaseFactory = GordianGenerator.createFactory();
final GordianRandomFactory myRandomFactory = myBaseFactory.getRandomFactory();

/* Create secureRandom */
final GordianRandomSpec mySpec = GordianRandomSpec.hash(GordianDigestSpec.sha2(GordianLength.LEN_256));
final SecureRandom myRandom = myRandomFactory.createRandom(mySpec);
